sys_dictionary.calculation:
(function () {
  var out = [];
  var gr = new GlideRecord('x_1782765_nowroom_ui_events'); // deine UI-Events
  gr.orderBy('start');
  gr.setLimit(200);
  gr.query();
  while (gr.next()) {
    var s = gr.getValue('start');
    var e = gr.getValue('end');
    if (!s || !e) continue;
    out.push({
      id: gr.getUniqueValue(),
      title: gr.getValue('title') || 'Event',
      start: s.replace(' ', 'T'),
      end:   e.replace(' ', 'T')
    });
  }
  return out;
})();
