sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

 var reason = '';

  try {
    // Fall A: Notification läuft auf sysapproval_approver
    if (current && current.getTableName && current.getTableName() == 'sysapproval_approver') {
      if (current.getValue('state') == 'rejected')
        reason = current.comments.getJournalEntry(1) || '';
    } else if (current) {
      // Fall B: Notification läuft auf der Reservation
      var appr = new GlideRecord('sysapproval_approver');
      appr.addQuery('document_id', current.getUniqueValue());  // approver gehört zu diesem Datensatz
      appr.addQuery('state', 'rejected');
      appr.orderByDesc('sys_updated_on'); // den neuesten Ablehn-Eintrag
      appr.setLimit(1);
      appr.query();
      if (appr.next()) {
        reason = appr.comments.getJournalEntry(1) || '';
      }
    }
  } catch (e) {
    // optional: nothing
  }

  template.print(reason || '');

})(current, template, email, email_action, event);
