sys_ux_client_script.script:
function evaluateProperty({ api /*, helpers */ }) {
  // Quelle: deine LookupMultipleRecords-Resource
  var list = (api && api.data && api.data.reservations_lmr && api.data.reservations_lmr.results) || [];

  function toISO(s){ if(!s) return ''; s=String(s); return s.indexOf(' ')>-1 ? s.replace(' ','T') : s; }

  var out = [];
  for (var i = 0; i < list.length; i++) {
    var r = list[i];

    var title =
      (r.number && r.number.value ? r.number.value : '') +
      ((r.room && r.room.displayValue) ? (' — ' + r.room.displayValue) : '');

    var start = toISO(r.planned_start && r.planned_start.value);
    var end   = toISO(r.planned_end   && r.planned_end.value);

    // ⬇️ HIER: sys_id sicher bestimmen + Record-URL bauen
    var sysId = (r.sys_id && r.sys_id.value) ||
                (r._row_data && r._row_data.uniqueValue) || '';

    var recordUrl = '/now/record/table/x_1782765_nowroom_reservation/' + sysId;

    // ⬇️ Und hier den bisherigen out.push(...) ersetzen
    out.push({
      id: sysId,                 // wichtig für den Kalender & Fallback
      title: title,
      start: start,
      end: end,
      extendedProps: {
        sys_id: sysId,           // zusätzlich verfügbar
        _recordUrl: recordUrl    // fertige Ziel-URL für den Klick
      }
    });
  }
  return out;
}
