sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  if (isLoading) return;

  var FIELD = 'planned_start';
  g_form.hideFieldMsg(FIELD, true);

  var val = g_form.getValue(FIELD);
  if (!val) return;

  // browser fitting ---> date (local)
  var parts = val.trim().split(' ');
  var d = parts[0].split('-');           // YYYY-MM-DD
  var t = (parts[1] || '00:00:00').split(':');  // HH:mm:ss
  var start = new Date(+d[0], +d[1]-1, +d[2], +(t[0]||0), +(t[1]||0), +(t[2]||0));
  var now   = new Date();

  if (start.getTime() < now.getTime()) {
    g_form.showFieldMsg(FIELD, 'Start date cannot be in the past.', 'error', true);
    // optional:
    // g_form.setValue(FIELD, oldValue);
  }
}
