sys_script_client.script:
function onSubmit() {
  var FIELD = 'planned_start';
  var val = g_form.getValue(FIELD);
  if (!val) return true; // no value -> not blocking at this point

  var parts = val.trim().split(' ');
  var d = parts[0].split('-');
  var t = (parts[1] || '00:00:00').split(':');
  var start = new Date(+d[0], +d[1]-1, +d[2], +(t[0]||0), +(t[1]||0), +(t[2]||0));
  var now   = new Date();

  if (start.getTime() < now.getTime()) {
    g_form.showFieldMsg(FIELD, 'Planned start cannot be in the past.', 'error', true);
    return false; // finally blocking submit
  }
  return true;
}
