sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var TABLE = 'x_1782765_nowroom_reservation';
	var START = 'planned_start';                  

	var now = new GlideDateTime();            

	var gr = new GlideRecord(TABLE);
	gr.addQuery(START, '>=', now);                // statt gs.nowDateTime()
	// gr.addQuery('state', '1');                 
	gr.orderBy(START);                        
	gr.setLimit(1);
	gr.query();

	var result = {};
	if (gr.next()) {
		result = {
			sys_id: gr.getUniqueValue(),
			start_time: gr.getDisplayValue(START),    // fÃ¼r UI
			start_time_sys: gr.getValue(START),       // system value 
			number: gr.getValue('number') || '',
			name: gr.getDisplayValue('name') || ''
		};
	} else {
		result = { message: 'No upcoming reservations found' };
	}

	return result;
})(request, response);
